mapper.select:
select count(1) from video_info


		and video_info.av = #{av}


		and video_info.is_delete = #{isDelete}


		and video_info.name like concat('%', #{name}, '%')


		and video_info.owner like concat('%', #{owner}, '%')


		and video_info.bv = #{bv}


		and video_info.type = #{type}


		and video_info.copyright = #{copyright}

select video_info.av, video_info.name, video_info.img, video_info.type, video_info.owner, video_info.copyright, video_info.pub_time as pubTime, video_info.start_time as StartTime, video_info.is_delete as isDelete, video_info.bv, video_info.description, video_info.state, video_info.duration, video_info.mission_id as MissionId, video_info.dynamic, video_info.start_page as StartPage from video_info
and video_info.av = #{av}
and video_info.is_delete = #{isDelete}
and video_info.name like concat('%', #{name}, '%')
and video_info.owner like concat('%', #{owner}, '%')
and video_info.bv = #{bv}
and video_info.type = #{type}
and video_info.copyright = #{copyright}
order by [${sorter}] ${sorted}
order by av desc
offset ${start} rows fetch next ${pageSize} rows only
